// Helper functions for filtering and querying
export const getTrekById = (id: string): Trek | undefined => {
  return TREKS.find(trek => trek.id === id);
};

export const getTreksByTier = (tier: 1 | 2 | 3): Trek[] => {
  return TREKS.filter(trek => trek.tier === tier);
};

export const getTreksByContinent = (continent: string): Trek[] => {
  return TREKS.filter(trek => trek.continent === continent);
};

export const getTreksByCountry = (country: string): Trek[] => {
  return TREKS.filter(trek => trek.country === country);
};

export const getTreksByDifficulty = (difficulty: string): Trek[] => {
  return TREKS.filter(trek => trek.difficulty === difficulty);
};

export const getTreksByAccommodation = (accommodation: string): Trek[] => {
  return TREKS.filter(trek => trek.accommodation === accommodation);
};

export const getTreksByDuration = (minDays: number, maxDays?: number): Trek[] => {
  if (maxDays) {
    return TREKS.filter(trek => trek.lengthDays >= minDays && trek.lengthDays <= maxDays);
  }
  return TREKS.filter(trek => trek.lengthDays >= minDays);
};

export const getPopularTreks = (limit: number = 10): Trek[] => {
  return [...TREKS]
    .sort((a, b) => b.popularityScore - a.popularityScore)
    .slice(0, limit);
};

export const searchTreks = (query: string): Trek[] => {
  const lowerQuery = query.toLowerCase();
  return TREKS.filter(trek => 
    trek.name.toLowerCase().includes(lowerQuery) ||
    trek.country.toLowerCase().includes(lowerQuery) ||
    trek.keyFeatures.toLowerCase().includes(lowerQuery) ||
    trek.terrain.toLowerCase().includes(lowerQuery)
  );
};

